using UnityEngine;using System.Collections;public class PatrolState : FSMState{    public PatrolState(Transform[] wp)     {         waypoints = wp;        stateID = FSMStateID.Patrolling;        curRotSpeed = 1.0f;        curSpeed = 100.0f;    }    public override void Reason(Transform player, Transform npc)    {        //Check the distance with player tank        //When the distance is near, transition to chase state        if (Vector3.Distance(npc.position, player.position) <= 300.0f)        {            Debug.Log("Switched to Chase State");            npc.GetComponent<BleddynController>().SetTransition(Transition.SawPlayer);        }    }    public override void Act(Transform player, Transform npc)    {            }}